mapper.update:
  	UPDATE BOARD
	SET BOARD_STATUS = 'N'
	WHERE BOARD_ID = #{boardId}

UPDATE MEDIA
SET ORIGIN_NAME = #{originName},
	CHANGE_NAME = #{changeName},
	FILE_PATH = #{filePath}
WHERE MEDIA_ID = #{mediaId}
  AND BOARD_ID = #{boardId}

mapper.select:
SELECT SCRAP_ID,
		SCRAP_STATUS
FROM SCRAP
WHERE MEMBERS_NO = ${scrapMembersNo}
AND BOARD_ID = ${scrapBoardId}

SELECT SCRAP_ID,
		SCRAP_STATUS
FROM SCRAP
WHERE MEMBERS_NO = ${scrapMembersNo}
AND MEDIA_ID = ${scrapMediaId}
